const API_URL = 'http://localhost:3000/mensagens'; 

// Envia mensagem para outro bot
async function enviarMensagem(de, para, mensagem) {
  try {
    const res = await axios.post(API_URL, { de, para, mensagem });
    console.log(`[${de}] Mensagem enviada para ${para}`);
  } catch (err) {
    console.error('Erro ao enviar mensagem:', err.message);
  }
}

// Busca mensagens para este bot
async function buscarMensagens(bot) {
  try {
    const res = await axios.get(`${API_URL}/${bot}`);
    return res.data;
  } catch (err) {
    console.error('Erro ao buscar mensagens:', err.message);
    return [];
  }
}

module.exports = { enviarMensagem, buscarMensagens };